        <tbody>
        {% for contact in contacts %}
            <tr>
                <td>{{ contact.subject }}</td>
                <td>{{ contact.createdAt ? contact.createdAt|date('d/m/Y') : '' }}</td>
                <td>{{ contact.resolved ? 'Yes' : 'No' }}</td>
                <td>
                    <a href=\"{{ path('app_contact_show', {'id': contact.id}) }}\">Consulter la demande</a>
                </td>
            </tr>
