        <tbody>
            <tr>
                <th>Sujet</th>
                <td>{{ contact.subject }}</td>
            </tr>
            <tr>
                <th>Contenu</th>
                <td>{{ contact.content }}</td>
            </tr>
            <tr>
                <th>Prénom</th>
                <td>{{ contact.name }}</td>
            </tr>
            <tr>
                <th>Email</th>
                <td>{{ contact.email }}</td>
            </tr>
            <tr>
                <th>Téléphone</th>
                <td>{{ contact.phone }}</td>
            </tr>
            <tr>
                <th>Créé le</th>
                <td>{{ contact.createdAt ? contact.createdAt|date('Y-m-d H:i:s') : '' }}</td>
            </tr>
